    public class DropBoxClass
    {
        private static string AccessToken { get; set; }
        private static DateTime GeneratedOn { get; set; }




        public static async void StartMakingTable(ITelegramBotClient botClient, Update update,
            CancellationToken cancellationToken, bool deleteBeforeMaking = false)
        {

            if (deleteBeforeMaking)
                try
                {
                    DeleteCreatedTable(botClient, update, cancellationToken);
                }
                catch 
                {
                    // ignored
                }

            await botClient.AnswerCallbackQueryAsync(
                callbackQueryId: update.CallbackQuery.Id,
                cancellationToken: cancellationToken
                );
            await botClient.DeleteMessageAsync(
                chatId: update.CallbackQuery.From.Id,
                messageId: update.CallbackQuery.Message.MessageId, 
                cancellationToken: cancellationToken);
            await botClient.SendTextMessageAsync(
                chatId: update.CallbackQuery.From.Id,
                text: $@"Please enter your DropBox email",
                replyMarkup: Buttons.EmailFRM(),
                cancellationToken: cancellationToken);
        }

        public static async void MakeFile(ITelegramBotClient botClient, Update update, CancellationToken cancellationToken)
        {
            //CheckAuth();
            var client = new DropboxClient(oauth2RefreshToken: AppSettings.Current.DropBoxData.RefreshKey, appKey: AppSettings.Current.DropBoxData.AppKey, appSecret: AppSettings.Current.DropBoxData.AppSecret);
            string[] scopes = { "account_info.read", "account_info.write", "contacts.read", "contacts.write", "file_requests.read", "file_requests.write", "files.content.write", "files.metadata.read", "files.metadata.write", "sharing.read", "sharing.write" };
            await client.RefreshAccessToken(scopes);
            long userId = update.Message.Chat.Id;

            const string sharedPhotoBase64 = @"";

            try
            {
                ExcelPackage.LicenseContext = LicenseContext.NonCommercial;
                Guid generatedGui = Guid.NewGuid();

                using (var package = new ExcelPackage(new FileInfo($"{generatedGui}.xlsx")))
                {
                    package.Workbook.Worksheets.Add("StockData");
                    package.Workbook.Worksheets["StockData"].Cells["A1"].LoadFromText("SKU,Condition,Quantity,Comment,Name,WH,Replacecs");
                    await package.SaveAsync(cancellationToken);
                }


                await using var stream = new StreamReader($"{generatedGui}.xlsx").BaseStream;
                var upload = await client.Files.UploadAsync($"{AppSettings.Current.DropBoxData.StockTablesPath}{userId}_stock.xlsx", body: stream);
                stream.Close();
                System.IO.File.Delete($"{generatedGui}.xlsx");


                string userDropboxEmail = update.Message.Text;

                List<Dropbox.Api.Sharing.MemberSelector> members = new()
                {
                    new Dropbox.Api.Sharing.MemberSelector.Email(userDropboxEmail)
                };


                // Sharing to a user
                var accLevel = Dropbox.Api.Sharing.AccessLevel.Editor.Instance;
                var accessLevelDone = await client.Sharing.AddFileMemberAsync($"{AppSettings.Current.DropBoxData.StockTablesPath}{userId}_stock.xlsx", members, accessLevel: accLevel);
                

                // Generating a link
                var sharedLinkData = await client.Sharing.GetFileMetadataAsync(
                    $"{AppSettings.Current.DropBoxData.StockTablesPath}{userId}_stock.xlsx");
                string sharedLinkTotal = sharedLinkData.PreviewUrl;

                





                var bytes = Convert.FromBase64String(sharedPhotoBase64);
                var contents = new MemoryStream(bytes);
                await botClient.SendPhotoAsync(
               chatId: update.Message.Chat.Id,
               photo: new Telegram.Bot.Types.InputFiles.InputOnlineFile(contents),
               caption: $@"A table was created for you. It will appear in the Shared section and will be called ""{userId}_stock.xlsx"".
The URL of your stock table is: {sharedLinkTotal}.
When you have finished filling the table, please come back to this menu and tap ""All done"".
",
               replyMarkup: Buttons.AlldoneButton(),
               cancellationToken: cancellationToken
               );



                var cs = @$"Server={AppSettings.Current.Database.Host};Port={AppSettings.Current.Database.Port};User={AppSettings.Current.Database.Login};Database={AppSettings.Current.Database.Database};Password={AppSettings.Current.Database.Password}";
                await using var con = new MySqlConnection(cs);
                await con.OpenAsync(cancellationToken);
                var result = await con.QueryFirstOrDefaultAsync($"UPDATE `userdata` SET `stockTableUrl`='{sharedLinkTotal}' WHERE `userId`='{userId}'");
                await con.CloseAsync(cancellationToken);
            }
            catch
            {
                await botClient.SendTextMessageAsync(
               chatId: update.Message.Chat.Id,
               text: $@"We got some error during making table.
",
               //replyMarkup: Buttons.AlldoneButton(),
               cancellationToken: cancellationToken
               );
                return;

            }



            //


            /*await botClient.SendTextMessageAsync(
               chatId: update.CallbackQuery.From.Id,
               text: $@"A table was created for you. It will appear in the Shared section and will be called ""{user_id}_stock.xlsx"".",
               cancellationToken: cancellationToken);*/

            //var g = client.Files.CreateFolderV2Async("/Stock tables/testme").Result;
        }

        public static async void FinishMakingTable(ITelegramBotClient botClient, Update update, CancellationToken cancellationToken)
        {
            await botClient.AnswerCallbackQueryAsync(
                callbackQueryId: update.CallbackQuery.Id,
                cancellationToken: cancellationToken
                );
            await botClient.DeleteMessageAsync(
                chatId: update.CallbackQuery.From.Id,
                messageId: update.CallbackQuery.Message.MessageId, 
                cancellationToken: cancellationToken);


            long userId = update.CallbackQuery.From.Id;

            var cs = @$"Server={AppSettings.Current.Database.Host};Port={AppSettings.Current.Database.Port};User={AppSettings.Current.Database.Login};Database={AppSettings.Current.Database.Database};Password={AppSettings.Current.Database.Password}";
            await using var con = new MySqlConnection(cs);
            await con.OpenAsync(cancellationToken);
            var result = await con.QueryFirstOrDefaultAsync($"UPDATE `userdata` SET `isHaveStockTable`=1 WHERE `userId`='{userId}'");
            await con.CloseAsync(cancellationToken);

            await botClient.SendTextMessageAsync(
                chatId: update.CallbackQuery.From.Id,
                text: $@"Table creating finished!",
                cancellationToken: cancellationToken);
        }


       private static async void DeleteCreatedTable(ITelegramBotClient botClient, Update update,
            CancellationToken cancellationToken)
        {
            var client = new DropboxClient(oauth2RefreshToken: AppSettings.Current.DropBoxData.RefreshKey, appKey: AppSettings.Current.DropBoxData.AppKey, appSecret: AppSettings.Current.DropBoxData.AppSecret);
            string[] scopes = { "account_info.read", "account_info.write", "contacts.read", "contacts.write", "file_requests.read", "file_requests.write", "files.content.write", "files.metadata.read", "files.metadata.write", "sharing.read", "sharing.write" };
            await client.RefreshAccessToken(scopes);

            long userId = update.CallbackQuery.From.Id;

            try
            {
                await client.Files.DeleteV2Async($"{AppSettings.Current.DropBoxData.StockTablesPath}{userId}_stock.xlsx");
            }
            catch
            {
                // ignored
            }

        }

    }
}
